import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import Catalogue from "./Catalogue";
import Welcome from "./Welcome"; // <-- Import Welcome from Welcome.js

// Modern font import (Google Fonts)
const fontLink = document.createElement("link");
fontLink.href = "https://fonts.googleapis.com/css2?family=Montserrat:wght@400;600;800&display=swap";
fontLink.rel = "stylesheet";
document.head.appendChild(fontLink);

const styles = {
  container: {
    minHeight: "100vh",
    background: "linear-gradient(135deg, #181c24 0%, #232b3b 100%)",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    fontFamily: "'Montserrat', Segoe UI, sans-serif",
    padding: "2rem",
  },
  card: {
    background: "rgba(30,34,44,0.97)",
    borderRadius: "22px",
    boxShadow: "0 8px 32px 0 rgba(31, 38, 135, 0.25)",
    padding: "2.5rem 2rem",
    maxWidth: 440,
    width: "100%",
    margin: "2rem 0",
    color: "#fff",
    backdropFilter: "blur(6px)",
  },
  title: {
    fontSize: "2.3rem",
    fontWeight: 800,
    color: "#a991f7",
    marginBottom: "1.5rem",
    textAlign: "center",
    letterSpacing: "2px",
    fontFamily: "'Montserrat', Segoe UI, sans-serif",
  },
  label: {
    display: "block",
    marginBottom: "0.5rem",
    fontWeight: 600,
    color: "#e0e0e0",
    fontFamily: "'Montserrat', Segoe UI, sans-serif",
  },
  input: {
    width: "100%",
    padding: "0.8rem",
    borderRadius: "10px",
    border: "1.5px solid #393e4a",
    marginBottom: "1.3rem",
    fontSize: "1.08rem",
    outline: "none",
    background: "#232b3b",
    color: "#fff",
    fontFamily: "'Montserrat', Segoe UI, sans-serif",
    transition: "border 0.2s",
  },
  button: {
    width: "100%",
    padding: "1rem",
    background: "linear-gradient(90deg, #a991f7 0%, #6e7ff3 100%)",
    color: "#fff",
    border: "none",
    borderRadius: "10px",
    fontWeight: 700,
    fontSize: "1.15rem",
    cursor: "pointer",
    marginTop: "0.7rem",
    boxShadow: "0 2px 12px rgba(118, 75, 162, 0.13)",
    transition: "background 0.2s",
    fontFamily: "'Montserrat', Segoe UI, sans-serif",
  },
  error: {
    color: "#ff6b81",
    background: "#2d1a1a",
    borderRadius: "10px",
    padding: "0.9rem",
    marginTop: "1rem",
    textAlign: "center",
    fontWeight: 600,
    fontFamily: "'Montserrat', Segoe UI, sans-serif",
  },
  result: {
    background: "rgba(40,44,54,0.97)",
    borderRadius: "18px",
    boxShadow: "0 2px 12px rgba(118, 75, 162, 0.13)",
    padding: "1.7rem",
    marginTop: "2rem",
    textAlign: "center",
    color: "#fff",
    fontFamily: "'Montserrat', Segoe UI, sans-serif",
  },
  breakdown: {
    textAlign: "left",
    margin: "1.2rem auto 0 auto",
    maxWidth: 320,
    fontSize: "1.08rem",
    color: "#e0e0e0",
    fontFamily: "'Montserrat', Segoe UI, sans-serif",
  },
};

const cityOptions = [
  "Amsterdam", "Athens", "Bangkok", "Barcelona", "Beijing", "Berlin", "Brussels", "Budapest",
  "Buenos Aires", "Cape Town", "Chicago", "Copenhagen", "Delhi", "Doha", "Dubai", "Dublin",
  "Edinburgh", "Helsinki", "Hong Kong", "Istanbul", "Jakarta", "Kuala Lumpur", "Las Vegas",
  "Lisbon", "London", "Los Angeles", "Madrid", "Manila", "Mexico City", "Milan", "Moscow",
  "Munich", "New York", "Osaka", "Paris", "Prague", "Rio de Janeiro", "Rome", "San Francisco",
  "Seoul", "Shanghai", "Singapore", "Stockholm", "Sydney", "Tel Aviv", "Tokyo", "Toronto",
  "Vancouver", "Vienna", "Warsaw", "Zurich"
];

function Estimator() {
  const [form, setForm] = useState({
    monthly_income: "",
    destination: "",
    origin: "",
    days: 7,
    people: 1,
  });
  const [result, setResult] = useState(null);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setResult(null);
    setLoading(true);
    try {
      const res = await fetch("http://localhost:5000/estimate", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });
      const data = await res.json();
      if (!res.ok) {
        setError(data.error || "Something went wrong");
      } else {
        setResult(data);
      }
    } catch (err) {
      setError("Could not connect to backend");
    }
    setLoading(false);
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <div style={styles.title}>✈️ Trip Savings Estimator</div>
        <form onSubmit={handleSubmit} autoComplete="off">
          <label style={styles.label}>Monthly Income (INR)</label>
          <input
            style={styles.input}
            type="number"
            name="monthly_income"
            value={form.monthly_income}
            onChange={handleChange}
            required
            min="1"
            placeholder="e.g. 50000"
          />
          <label style={styles.label}>Origin Airport (IATA code)</label>
          <input
            type="text"
            name="origin"
            value={form.origin}
            onChange={handleChange}
            required
            placeholder="e.g. DEL"
            maxLength={3}
            style={{ ...styles.input, textTransform: "uppercase" }}
          />
          <label style={styles.label}>Destination City</label>
          <select
            name="destination"
            value={form.destination}
            onChange={handleChange}
            required
            style={styles.input}
          >
            <option value="">Select a city</option>
            {cityOptions.map(city => (
              <option key={city} value={city}>{city}</option>
            ))}
          </select>
          <label style={styles.label}>Number of Days</label>
          <input
            style={styles.input}
            type="number"
            name="days"
            value={form.days}
            onChange={handleChange}
            min="1"
            required
          />
          <label style={styles.label}>Number of People</label>
          <input
            style={styles.input}
            type="number"
            name="people"
            value={form.people}
            onChange={handleChange}
            min="1"
            required
          />
          <button type="submit" style={styles.button} disabled={loading}>
            {loading ? "Estimating..." : "Estimate"}
          </button>
        </form>
        {error && <div style={styles.error}>{error}</div>}
        {result && (
          <div style={styles.result}>
            <h3>
              Results for <span style={{ color: "#a991f7" }}>{result.destination}</span>
            </h3>
            <p style={{ fontSize: "1.3rem", margin: "1rem 0", color: "#6e7ff3" }}>
              <b>Total Trip Cost:</b> ₹{result.total_trip_cost}
            </p>
            <p style={{ fontSize: "1.1rem" }}>
              <b>Months to Save:</b>{" "}
              <span style={{ color: "#ffb86b", fontWeight: 700 }}>{result.months_to_save}</span>
            </p>
            <div style={styles.breakdown}>
              <h4 style={{ marginBottom: "0.5rem", color: "#a991f7" }}>Breakdown:</h4>
              <ul style={{ paddingLeft: "1.2rem" }}>
                <li>
                  <b>Hotel:</b> ₹{result.details.hotel_cost}
                </li>
                <li>
                  <b>Meals:</b> ₹{result.details.meal_cost}
                </li>
                <li>
                  <b>Flights:</b> ₹{result.details.flight_cost}
                </li>
              </ul>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

