import React, { useState } from "react";

const styles = {
  container: {
    minHeight: "100vh",
    background: "linear-gradient(135deg, #e0eafc 0%, #cfdef3 100%)",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    fontFamily: "Segoe UI, sans-serif",
    padding: "2rem",
  },
  card: {
    background: "#fff",
    borderRadius: "18px",
    boxShadow: "0 8px 32px 0 rgba(31, 38, 135, 0.15)",
    padding: "2.5rem 2rem",
    maxWidth: 420,
    width: "100%",
    margin: "1rem 0",
  },
  title: {
    fontSize: "2rem",
    fontWeight: 700,
    color: "#2d3a4b",
    marginBottom: "1.5rem",
    textAlign: "center",
    letterSpacing: "1px",
  },
  label: {
    display: "block",
    marginBottom: "0.5rem",
    fontWeight: 500,
    color: "#3a4a5d",
  },
  input: {
    width: "100%",
    padding: "0.7rem",
    borderRadius: "8px",
    border: "1px solid #bfc9d9",
    marginBottom: "1.2rem",
    fontSize: "1rem",
    outline: "none",
    transition: "border 0.2s",
  },
  button: {
    width: "100%",
    padding: "0.9rem",
    background: "linear-gradient(90deg, #667eea 0%, #764ba2 100%)",
    color: "#fff",
    border: "none",
    borderRadius: "8px",
    fontWeight: 600,
    fontSize: "1.1rem",
    cursor: "pointer",
    marginTop: "0.5rem",
    boxShadow: "0 2px 8px rgba(118, 75, 162, 0.08)",
    transition: "background 0.2s",
  },
  error: {
    color: "#e74c3c",
    background: "#fff0f0",
    borderRadius: "8px",
    padding: "0.8rem",
    marginTop: "1rem",
    textAlign: "center",
    fontWeight: 500,
  },
  result: {
    background: "#f7fafc",
    borderRadius: "14px",
    boxShadow: "0 2px 8px rgba(118, 75, 162, 0.06)",
    padding: "1.5rem",
    marginTop: "2rem",
    textAlign: "center",
  },
  breakdown: {
    textAlign: "left",
    margin: "1rem auto 0 auto",
    maxWidth: 300,
    fontSize: "1rem",
    color: "#3a4a5d",
  },
};

const cityOptions = [
  "Amsterdam", "Athens", "Bangkok", "Barcelona", "Beijing", "Berlin", "Brussels", "Budapest",
  "Buenos Aires", "Cape Town", "Chicago", "Copenhagen", "Delhi", "Doha", "Dubai", "Dublin",
  "Edinburgh", "Helsinki", "Hong Kong", "Istanbul", "Jakarta", "Kuala Lumpur", "Las Vegas",
  "Lisbon", "London", "Los Angeles", "Madrid", "Manila", "Mexico City", "Milan", "Moscow",
  "Munich", "New York", "Osaka", "Paris", "Prague", "Rio de Janeiro", "Rome", "San Francisco",
  "Seoul", "Shanghai", "Singapore", "Stockholm", "Sydney", "Tel Aviv", "Tokyo", "Toronto",
  "Vancouver", "Vienna", "Warsaw", "Zurich"
  // ...add more if your dataset has more
];

function App() {
  const [form, setForm] = useState({
    monthly_income: "",
    destination: "",
    origin: "",
    days: 7,
    people: 1,
  });
  const [result, setResult] = useState(null);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setResult(null);
    setLoading(true);
    try {
      const res = await fetch("http://localhost:5000/estimate", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });
      const data = await res.json();
      if (!res.ok) {
        setError(data.error || "Something went wrong");
      } else {
        setResult(data);
      }
    } catch (err) {
      setError("Could not connect to backend");
    }
    setLoading(false);
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <div style={styles.title}>✈️ Trip Savings Estimator</div>
        <form onSubmit={handleSubmit} autoComplete="off">
          <label style={styles.label}>Monthly Income (USD)</label>
          <input
            style={styles.input}
            type="number"
            name="monthly_income"
            value={form.monthly_income}
            onChange={handleChange}
            required
            min="1"
            placeholder="e.g. 3000"
          />
          <label style={styles.label}>Origin Airport (IATA code)</label>
          <input
            type="text"
            name="origin"
            value={form.origin}
            onChange={handleChange}
            required
            placeholder="e.g. JFK"
            maxLength={3}
            style={{ ...styles.input, textTransform: "uppercase" }}
          />
          <label style={styles.label}>Destination City</label>
          <select
            name="destination"
            value={form.destination}
            onChange={handleChange}
            required
            style={styles.input}
          >
            <option value="">Select a city</option>
            {cityOptions.map(city => (
              <option key={city} value={city}>{city}</option>
            ))}
          </select>
          <label style={styles.label}>Number of Days</label>
          <input
            style={styles.input}
            type="number"
            name="days"
            value={form.days}
            onChange={handleChange}
            min="1"
            required
          />
          <label style={styles.label}>Number of People</label>
          <input
            style={styles.input}
            type="number"
            name="people"
            value={form.people}
            onChange={handleChange}
            min="1"
            required
          />
          <button type="submit" style={styles.button} disabled={loading}>
            {loading ? "Estimating..." : "Estimate"}
          </button>
        </form>
        {error && <div style={styles.error}>{error}</div>}
        {result && (
          <div style={styles.result}>
            <h3>
              Results for <span style={{ color: "#764ba2" }}>{result.destination}</span>
            </h3>
            <p style={{ fontSize: "1.2rem", margin: "1rem 0" }}>
              <b>Total Trip Cost:</b> <span style={{ color: "#667eea" }}>${result.total_trip_cost}</span>
            </p>
            <p style={{ fontSize: "1.1rem" }}>
              <b>Months to Save:</b>{" "}
              <span style={{ color: "#e67e22", fontWeight: 700 }}>{result.months_to_save}</span>
            </p>
            <div style={styles.breakdown}>
              <h4 style={{ marginBottom: "0.5rem" }}>Breakdown:</h4>
              <ul style={{ paddingLeft: "1.2rem" }}>
                <li>
                  <b>Hotel:</b> ${result.details.hotel_cost}
                </li>
                <li>
                  <b>Meals:</b> ${result.details.meal_cost}
                </li>
                <li>
                  <b>Flights:</b> ${result.details.flight_cost}
                </li>
              </ul>
            </div>
          </div>
        )}
      </div>
      <footer style={{ marginTop: "2rem", color: "#888", fontSize: "0.95rem" }}>
        Made with <span style={{ color: "#764ba2" }}>♥</span> for your next adventure!
      </footer>
    </div>
  );
}

export default App;
