import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import Catalogue from "./Catalogue";

const styles = {
  container: {
    minHeight: "100vh",
    background: "linear-gradient(135deg, #e0eafc 0%, #cfdef3 100%)",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    fontFamily: "Segoe UI, sans-serif",
    padding: "2rem",
  },
  card: {
    background: "#fff",
    borderRadius: "18px",
    boxShadow: "0 8px 32px 0 rgba(31, 38, 135, 0.15)",
    padding: "2.5rem 2rem",
    maxWidth: 420,
    width: "100%",
    margin: "1rem 0",
  },
  title: {
    fontSize: "2rem",
    fontWeight: 700,
    color: "#2d3a4b",
    marginBottom: "1.5rem",
    textAlign: "center",
    letterSpacing: "1px",
  },
  label: {
    display: "block",
    marginBottom: "0.5rem",
    fontWeight: 500,
    color: "#3a4a5d",
  },
  input: {
    width: "100%",
    padding: "0.7rem",
    borderRadius: "8px",
    border: "1px solid #bfc9d9",
    marginBottom: "1.2rem",
    fontSize: "1rem",
    outline: "none",
    transition: "border 0.2s",
  },
  button: {
    width: "100%",
    padding: "0.9rem",
    background: "linear-gradient(90deg, #667eea 0%, #764ba2 100%)",
    color: "#fff",
    border: "none",
    borderRadius: "8px",
    fontWeight: 600,
    fontSize: "1.1rem",
    cursor: "pointer",
    marginTop: "0.5rem",
    boxShadow: "0 2px 8px rgba(118, 75, 162, 0.08)",
    transition: "background 0.2s",
  },
  error: {
    color: "#e74c3c",
    background: "#fff0f0",
    borderRadius: "8px",
    padding: "0.8rem",
    marginTop: "1rem",
    textAlign: "center",
    fontWeight: 500,
  },
  result: {
    background: "#f7fafc",
    borderRadius: "14px",
    boxShadow: "0 2px 8px rgba(118, 75, 162, 0.06)",
    padding: "1.5rem",
    marginTop: "2rem",
    textAlign: "center",
  },
  breakdown: {
    textAlign: "left",
    margin: "1rem auto 0 auto",
    maxWidth: 300,
    fontSize: "1rem",
    color: "#3a4a5d",
  },
};

const cityOptions = [
  "Amsterdam", "Athens", "Bangkok", "Barcelona", "Beijing", "Berlin", "Brussels", "Budapest",
  "Buenos Aires", "Cape Town", "Chicago", "Copenhagen", "Delhi", "Doha", "Dubai", "Dublin",
  "Edinburgh", "Helsinki", "Hong Kong", "Istanbul", "Jakarta", "Kuala Lumpur", "Las Vegas",
  "Lisbon", "London", "Los Angeles", "Madrid", "Manila", "Mexico City", "Milan", "Moscow",
  "Munich", "New York", "Osaka", "Paris", "Prague", "Rio de Janeiro", "Rome", "San Francisco",
