function Estimator() {
  const [form, setForm] = useState({
    monthly_income: "",
    destination: "",
    origin: "",
    days: 7,
    people: 1,
  });
  const [result, setResult] = useState(null);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setResult(null);
    setLoading(true);
    try {
      const res = await fetch("http://localhost:5000/estimate", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });
      const data = await res.json();
      if (!res.ok) {
        setError(data.error || "Something went wrong");
      } else {
        setResult(data);
      }
    } catch (err) {
      setError("Could not connect to backend");
    }
    setLoading(false);
  };

  return (
    <div style={styles.card}>
      <div style={styles.title}>✈️ Trip Savings Estimator</div>
      <form onSubmit={handleSubmit} autoComplete="off">
        <label style={styles.label}>Monthly Income (INR)</label>
        <input
          style={styles.input}
          type="number"
          name="monthly_income"
          value={form.monthly_income}
          onChange={handleChange}
          required
          min="1"
          placeholder="e.g. 50000"
        />
        <label style={styles.label}>Origin Airport (IATA code)</label>
        <input
          type="text"
          name="origin"
          value={form.origin}
          onChange={handleChange}
          required
          placeholder="e.g. JFK"
          maxLength={3}
          style={{ ...styles.input, textTransform: "uppercase" }}
        />
        <label style={styles.label}>Destination City</label>
        <select
          name="destination"
          value={form.destination}
          onChange={handleChange}
          required
          style={styles.input}
        >
          <option value="">Select a city</option>
          {cityOptions.map(city => (
            <option key={city} value={city}>{city}</option>
          ))}
        </select>
        <label style={styles.label}>Number of Days</label>
        <input
          style={styles.input}
          type="number"
          name="days"
          value={form.days}
          onChange={handleChange}
          min="1"
          required
        />
        <label style={styles.label}>Number of People</label>
        <input
          style={styles.input}
          type="number"
          name="people"
          value={form.people}
          onChange={handleChange}
          min="1"
          required
        />
        <button type="submit" style={styles.button} disabled={loading}>
          {loading ? "Estimating..." : "Estimate"}
        </button>
      </form>
      {error && <div style={styles.error}>{error}</div>}
      {result && (
        <div style={styles.result}>
          <h3>
            Results for <span style={{ color: "#764ba2" }}>{result.destination}</span>
          </h3>
          <p style={{ fontSize: "1.2rem", margin: "1rem 0" }}>
            <b>Total Trip Cost:</b> <span style={{ color: "#667eea" }}>₹{result.total_trip_cost}</span>
          </p>
          <p style={{ fontSize: "1.1rem" }}>
            <b>Months to Save:</b>{" "}
            <span style={{ color: "#e67e22", fontWeight: 700 }}>{result.months_to_save}</span>
          </p>
          <div style={styles.breakdown}>
            <h4 style={{ marginBottom: "0.5rem" }}>Breakdown:</h4>
            <ul style={{ paddingLeft: "1.2rem" }}>
              <li>
                <b>Hotel:</b> ₹{result.details.hotel_cost}
              </li>
              <li>
                <b>Meals:</b> ₹{result.details.meal_cost}
              </li>
              <li>
                <b>Flights:</b> ₹{result.details.flight_cost}
              </li>
            </ul>
          </div>
        </div>
      )}
    </div>
  );
}

