import React from "react";
import Card from "./components/Card";
import GradientButton from "./components/GradientButton";
import { FaGlobeAmericas } from "react-icons/fa";
import { Link } from "react-router-dom";

export default function Welcome() {
  return (
    <div style={{
      minHeight: "100vh",
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      justifyContent: "center",
      background: "linear-gradient(135deg, #232b3b 0%, #181c24 100%)"
    }}>
      <Card style={{ textAlign: "center" }}>
        <FaGlobeAmericas size={48} color="#a991f7" style={{ marginBottom: 16 }} />
        <h1 style={{
          fontSize: "2.5rem",
          fontWeight: 800,
          color: "#a991f7",
          marginBottom: "1.2rem"
        }}>
          Welcome to Trip Estimator
        </h1>
        <p style={{ color: "#e0e0e0", fontSize: "1.2rem", marginBottom: "2rem" }}>
          Plan your dream trip, estimate costs, and discover amazing destinations!
        </p>
        <Link to="/estimator">
          <GradientButton>Start Estimating</GradientButton>
        </Link>
      </Card>
    </div>
  );
}
